const fs = require('fs'),
  csv = require('./index');

var filePath = process.argv[2];

var row = 0;
var last;

fs.createReadStream(filePath)
  .pipe(csv.parse({ headers: false }))
  .on('data', function(d){
    row += 1;
    console.log("row", row, d);
    if(!d[0]){
      console.log(`Failure in Row: ${row}`, d, last);
    }
    last = d;
  });
